import React, { useState } from "react";
import "./WelcomePage.css";

const WelcomePage = ({
  onAuthSuccess,
  isLoading,
  setIsLoading,
  error,
  setError,
}) => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const calculateStrength = (pw) => {
    let score = 0;
    if (!pw) return 0;
    if (pw.length >= 8) score++;
    if (/[A-Z]/.test(pw)) score++;
    if (/[0-9]/.test(pw)) score++;
    if (/[^A-Za-z0-9]/.test(pw)) score++;
    return Math.min(3, score);
  };

  const strengthLabels = ["", "Weak", "Fair", "Strong"];
  const passwordStrength = calculateStrength(password);
  const passwordStrengthLabel = strengthLabels[passwordStrength];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError("Please fill in all fields");
      return;
    }

    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    // For sign up, enforce a strong password (score 3)
    if (!isLogin && passwordStrength < 3) {
      setError("Please choose a stronger password: min 8 chars, include uppercase, number and symbol.");
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const endpoint = isLogin ? '/api/auth/login' : '/api/auth/signup';
      console.log('Attempting to connect to:', endpoint);

      const res = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      console.log('Response status:', res.status);
      const data = await res.json();
      console.log('Response data:', data);

      if (!res.ok) {
        setError(data?.error || `${isLogin ? "Login" : "Signup"} failed`);
        return;
      }

      onAuthSuccess(data);
    } catch (err) {
      console.error('Network error details:', err);
      setError(`Connection failed. Make sure the server is running on the correct port.`);
    } finally {
      setIsLoading(false);
    }
  };

  const switchMode = () => {
    setIsLogin(!isLogin);
    setError(null);
    setPassword("");
    setConfirmPassword("");
  };

  const socialSignIn = (provider) => {
    setError(null);
    const urls = {
      google: process.env.REACT_APP_GOOGLE_AUTH_URL,
      github: process.env.REACT_APP_GITHUB_AUTH_URL,
    };
    const url = urls[provider];
    if (!url) {
      setError(`${provider[0].toUpperCase() + provider.slice(1)} sign-in is not configured. Please set REACT_APP_${provider.toUpperCase()}_AUTH_URL`);
      return;
    }

    const popup = window.open(url, `${provider}-auth`, 'width=600,height=700');
    if (!popup) {
      setError(`Popup blocked. Please allow popups to sign in with ${provider}.`);
      return;
    }

    let cleaned = false;
    const msgHandler = (e) => {
      try {
        const data = e.data;
        if (data && data.token) {
          cleaned = true;
          window.removeEventListener('message', msgHandler);
          if (!popup.closed) popup.close();
          onAuthSuccess(data);
        }
      } catch (err) {
        // ignore
      }
    };

    window.addEventListener('message', msgHandler);

    const checkInterval = setInterval(() => {
      if (popup.closed) {
        clearInterval(checkInterval);
        window.removeEventListener('message', msgHandler);
        if (!cleaned) {
          setError('Sign-in popup closed. If sign-in succeeded, you should be logged in automatically; otherwise check server configuration or allow popups.');
        }
      }
    }, 500);
  }; 

  return (
    <div className="welcome-page">
      <div className="welcome-background">
        {["üçï", "üçù", "ü•ó", "üç∞", "üçú", "ü•ò"].map((f, i) => (
          <div key={i} className="floating-food">{f}</div>
        ))}
      </div>

      <div className="welcome-container">
        <div className="welcome-header">
          <div className="welcome-logo">
            <div className="logo-icon">RH</div>
            <div className="title-row">
              <h1>Recipe Haven</h1>
              <span className="dev-badge">Developed by Rounak</span>
            </div>
            <p className="welcome-subtitle">
              Discover, save, and create amazing recipes from around the world ‚Äî smart suggestions, personal collections, and effortless recipe creation.
            </p>

            <div className="quick-features" aria-hidden>
              <div className="chip">üîç Smart Search</div>
              <div className="chip">‚ù§Ô∏è Save & Sync</div>
              <div className="chip">‚úçÔ∏è Create & Share</div>
            </div>
          </div>
        </div>

        <div className="auth-card">
          <div className="auth-tabs">
            <button
              className={`auth-tab ${isLogin ? "active" : ""}`}
              onClick={() => setIsLogin(true)}
            >
              Sign In
            </button>
            <button
              className={`auth-tab ${!isLogin ? "active" : ""}`}
              onClick={() => setIsLogin(false)}
            >
              Sign Up
            </button>
          </div>

          <div className="social-row">
            <button type="button" className="social-btn google" aria-label="Sign in with Google" onClick={() => socialSignIn('google')}>Sign in with Google</button>
            <button type="button" className="social-btn github" aria-label="Sign in with GitHub" onClick={() => socialSignIn('github')}>Sign in with GitHub</button>
          </div>

          <div className="social-help">
            {!process.env.REACT_APP_GOOGLE_AUTH_URL && (
              <div>Google sign-in is not configured. Set <code>REACT_APP_GOOGLE_AUTH_URL</code> in your <code>.env</code></div>
            )}
            {!process.env.REACT_APP_GITHUB_AUTH_URL && (
              <div>GitHub sign-in is not configured. Set <code>REACT_APP_GITHUB_AUTH_URL</code> in your <code>.env</code></div>
            )}
          </div> 

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label>Username</label>
              <input
                className="form-input"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                disabled={isLoading}
                placeholder="Enter your username"
              />
            </div>

            <div className="form-group password-group">
              <label>Password</label>
              <div className="password-field">
                <input
                  className="form-input"
                  type={showPassword ? "text" : "password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  disabled={isLoading}
                  placeholder="Enter your password"
                  aria-label="Password"
                />
                <button
                  type="button"
                  className="show-pass-btn"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-pressed={showPassword}
                  aria-label={showPassword ? "Hide password" : "Show password"}
                >
                  {showPassword ? "üôà" : "üëÅ"}
                </button>
              </div>

              {!isLogin && (
                <div className="strength-meter">
                  <div className={`strength-bar s${passwordStrength}`} aria-hidden></div>
                <div className="strength-label">{password ? passwordStrengthLabel : ""}</div>
                </div>
              )}
            </div>

            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  className="form-input"
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  disabled={isLoading}
                  placeholder="Confirm your password"
                />
              </div>
            )}

            {error && <div className="error-message">‚ö†Ô∏è {error}</div>}

            <button type="submit" disabled={isLoading} className="auth-submit-btn">
              {isLoading
                ? isLogin
                  ? "Signing In..."
                  : "Creating Account..."
                : isLogin
                ? "üöÄ Sign In"
                : "‚ú® Create Account"}
            </button>

            <div style={{display: 'flex', gap: 8, justifyContent: 'center', marginTop: 12}}>
              <button
                type="button"
                className="auth-guest-btn"
                onClick={() => {
                  setError(null);
                  setIsLoading(false);
                  onAuthSuccess({ token: '', id: 'guest', username: 'Guest' });
                }}
              >
                Continue as Guest
              </button>
            </div>
          </form>

          <div className="auth-switch">
            <p>
              {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
              <button onClick={switchMode} className="switch-btn">
                {isLogin ? "Sign up here" : "Sign in here"}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomePage;
