import React, { useState } from "react";
import "./App.css";

const API_BASE =
  process.env.REACT_APP_API_URL || "http://localhost:3000";

const SavedRecipes = ({ open, onClose, favorites = [], token, refresh }) => {
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editImage, setEditImage] = useState("");
  const [editCalories, setEditCalories] = useState("");
  const [editIngredients, setEditIngredients] = useState("");
  const [saving, setSaving] = useState(false);

  if (!open) return null;

  const startEdit = (item) => {
    setEditingId(item.id);
    setEditTitle(item.title || "");
    setEditCalories(item.calories?.toString() || "");
    setEditImage(item.image || "");
    setEditIngredients(
      (item.ingredients || []).map((i) => i.text).join("\n")
    );
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditTitle("");
    setEditCalories("");
    setEditImage("");
    setEditIngredients("");
  };

  const saveEdit = async (id) => {
    try {
      setSaving(true);

      const ingredients = editIngredients
        .split("\n")
        .map((l) => l.trim())
        .filter(Boolean)
        .map((text) => ({ text }));

      const res = await fetch(`${API_BASE}/api/recipes/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          ...(token ? { Authorization: `Bearer ${token}` } : {}),
        },
        body: JSON.stringify({
          title: editTitle,
          calories: editCalories ? Number(editCalories) : null,
          image: editImage || null,
          ingredients,
        }),
      });

      if (!res.ok) throw new Error("Update failed");

      await refresh();
      cancelEdit();
    } catch (err) {
      alert("Failed to update recipe");
      console.error(err);
    } finally {
      setSaving(false);
    }
  };

  const onDelete = async (id) => {
    if (!window.confirm("Delete this saved recipe?")) return;

    try {
      const res = await fetch(`${API_BASE}/api/recipes/${id}`, {
        method: "DELETE",
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });

      if (!res.ok) throw new Error("Delete failed");

      await refresh();
    } catch (err) {
      alert("Failed to delete recipe");
      console.error(err);
    }
  };

  return (
    <div className="modal-backdrop" onClick={!saving ? onClose : undefined}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <h3>Saved Recipes</h3>
          <button className="btn" onClick={onClose} disabled={saving}>
            Close
          </button>
        </div>

        {favorites.length === 0 && <p>No saved recipes.</p>}

        <ul style={{ listStyle: "none", padding: 0, marginTop: 12 }}>
          {favorites.map((item) => (
            <li
              key={item.id}
              style={{
                padding: 12,
                borderBottom: "1px solid rgba(255,255,255,0.05)",
                display: "flex",
                justifyContent: "space-between",
              }}
            >
              <div style={{ flex: 1 }}>
                {editingId === item.id ? (
                  <div style={{ display: "grid", gap: 6 }}>
                    <input
                      value={editTitle}
                      onChange={(e) => setEditTitle(e.target.value)}
                      placeholder="Title"
                    />
                    <input
                      value={editImage}
                      onChange={(e) => setEditImage(e.target.value)}
                      placeholder="Image URL"
                    />
                    <input
                      type="number"
                      value={editCalories}
                      onChange={(e) => setEditCalories(e.target.value)}
                      placeholder="Calories"
                    />
                    <textarea
                      rows={4}
                      value={editIngredients}
                      onChange={(e) => setEditIngredients(e.target.value)}
                      placeholder="One ingredient per line"
                    />
                  </div>
                ) : (
                  <>
                    <div style={{ fontWeight: 700 }}>{item.title}</div>
                    <div style={{ fontSize: 12, color: "#9fb0c8" }}>
                      {Math.round(item.calories || 0)} cal â€¢{" "}
                      {(item.ingredients || []).length} ingredients
                    </div>
                  </>
                )}
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                {editingId === item.id ? (
                  <>
                    <button
                      className="btn"
                      onClick={() => saveEdit(item.id)}
                      disabled={saving}
                    >
                      Save
                    </button>
                    <button className="btn" onClick={cancelEdit}>
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <button className="btn" onClick={() => startEdit(item)}>
                      Edit
                    </button>
                    <button
                      className="btn"
                      onClick={() => onDelete(item.id)}
                    >
                      Delete
                    </button>
                  </>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default SavedRecipes;
