import React, { useState } from 'react';
import './App.css';

const CreateRecipeModal = ({ open, onClose, token, onCreated }) => {
  const [title, setTitle] = useState('');
  const [image, setImage] = useState('');
  const [calories, setCalories] = useState('');
  const [ingredientsText, setIngredientsText] = useState('');
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);

  if (!open) return null;

  const handleSave = async () => {
    setError(null);
    if (!title.trim()) {
      setError('Title is required');
      return;
    }

    const ingredients = ingredientsText
      .split('\n')
      .map((l) => l.trim())
      .filter(Boolean)
      .map((text) => ({ text }));

    const payload = {
      title: title.trim(),
      image: image.trim() || null,
      calories: calories ? Number(calories) : null,
      ingredients,
    };

    try {
      setSaving(true);
      const headers = { 'Content-Type': 'application/json' };
      if (token) headers.Authorization = `Bearer ${token}`;

      const res = await fetch('/api/recipes', { method: 'POST', headers, body: JSON.stringify(payload) });
      if (!res.ok) {
        const body = await res.json().catch(() => ({}));
        throw new Error(body.error || 'Failed to save recipe');
      }

      const data = await res.json();
      onCreated && onCreated(data);
      // reset form
      setTitle('');
      setImage('');
      setCalories('');
      setIngredientsText('');
      onClose();
    } catch (err) {
      setError(err.message || 'Network error');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
          <h3>Create Recipe</h3>
          <div>
            <button className="btn" onClick={onClose}>Close</button>
          </div>
        </div>

        {error && <div style={{color:'#ffb3b3',marginBottom:8}}>{error}</div>}

        <div style={{display:'grid',gap:8}}>
          <input placeholder="Title" value={title} onChange={(e)=>setTitle(e.target.value)} />
          <input placeholder="Image URL" value={image} onChange={(e)=>setImage(e.target.value)} />
          <input placeholder="Calories" value={calories} onChange={(e)=>setCalories(e.target.value)} />
          <textarea placeholder="Ingredients (one per line)" value={ingredientsText} onChange={(e)=>setIngredientsText(e.target.value)} rows={6} />
          <div style={{display:'flex',gap:8,justifyContent:'flex-end'}}>
            <button className="btn" onClick={onClose}>Cancel</button>
            <button className="btn" onClick={handleSave} disabled={saving}>{saving ? 'Saving...' : 'Create'}</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateRecipeModal;
