import React, { useEffect, useState } from 'react';
import './App.css';
import Recipe from './Recipe';
import SavedRecipes from './SavedRecipes';
import CreateRecipeModal from './CreateRecipeModal';
import WelcomePage from './WelcomePage';

const App = () => {
  // Use environment variables for credentials (read at build time)
  const APP_ID = process.env.REACT_APP_EDAMAM_ID;
  const APP_KEY = process.env.REACT_APP_EDAMAM_KEY;

  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('chicken');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favoritesCount, setFavoritesCount] = useState(0);
  const [isSearchFocused, setIsSearchFocused] = useState(false);

  const [showSaved, setShowSaved] = useState(false);
  const [showCreate, setShowCreate] = useState(false);

  const [ownerId, setOwnerId] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [token, setToken] = useState(window.localStorage.getItem('token') || null);
  const [authUser, setAuthUser] = useState(null);
  const [isAuthLoading, setIsAuthLoading] = useState(false);
  const [authError, setAuthError] = useState(null);
  const [showWelcome, setShowWelcome] = useState(!token);

  useEffect(() => {
    // Ensure ownerId (per-browser user identifier)
    let owner = window.localStorage.getItem('ownerId');
    if (!owner) {
      owner = `anon_${Math.random().toString(36).slice(2, 10)}`;
      window.localStorage.setItem('ownerId', owner);
    }
    setOwnerId(owner);
  }, []);

  // Define fetchFavorites function
  const fetchFavorites = async (tokenArg = token) => {
    try {
      const currentToken = tokenArg || token;
      // if token present, verify and fetch server-side favorites
      if (currentToken) {
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 5000);
        
        try {
          const meRes = await fetch('/api/auth/me', { 
            headers: { Authorization: `Bearer ${currentToken}` },
            signal: controller.signal
          });
          clearTimeout(timeoutId);
          
          if (meRes.ok) {
            const me = await meRes.json();
            setAuthUser(me);
          } else {
            setToken(null);
            window.localStorage.removeItem('token');
          }

          const listRes = await fetch('/api/recipes', { 
            headers: { Authorization: `Bearer ${currentToken}` },
            signal: controller.signal
          });
          if (listRes.ok) {
            const items = await listRes.json();
            setFavorites(items);
            setFavoritesCount(items.length);
            return;
          }
        } catch (fetchError) {
          clearTimeout(timeoutId);
          if (fetchError.name === 'AbortError') {
            // Request timed out, fall back to localStorage
          } else {
            throw fetchError;
          }
        }

        // if server returns 503 or not ok, continue to fallback to localStorage
      }
    } catch (e) {
      // ignore network errors and fallback
    }

    // fallback to localStorage
    try {
      const favs = JSON.parse(window.localStorage.getItem('favorites') || '[]');
      setFavoritesCount(favs.length);
      setFavorites(favs);
    } catch (e) {
      setFavoritesCount(0);
      setFavorites([]);
    }
  };

  useEffect(() => {
    if (token && !showWelcome) {
      fetchFavorites();
    }

    const onStorage = () => {
      const updated = JSON.parse(window.localStorage.getItem('favorites') || '[]');
      setFavoritesCount(updated.length);
      setFavorites(updated);
    };
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token, showWelcome]);

  const saveRecipe = async (recipe) => {
    const payload = { ...recipe };
    try {
      let headers = { 'Content-Type': 'application/json' };
      if (token) headers.Authorization = `Bearer ${token}`;
      else payload.ownerId = ownerId;

      const res = await fetch('/api/recipes', { method: 'POST', headers, body: JSON.stringify(payload) });
      if (res.ok) {
        const item = await res.json();
        // refresh from server to get canonical list
        await fetchFavorites();
        return item;
      }
    } catch (e) {
      // fallback to localStorage
      const favs = JSON.parse(window.localStorage.getItem('favorites') || '[]');
      favs.push(payload);
      window.localStorage.setItem('favorites', JSON.stringify(favs));
      setFavorites(favs);
      setFavoritesCount(favs.length);
    }
  };

  const removeRecipe = async (id, title) => {
    try {
      let headers = {};
      if (token) headers.Authorization = `Bearer ${token}`;

      const res = await fetch(`/api/recipes/${id}`, { method: 'DELETE', headers });
      if (res.ok) {
        setFavorites((s) => s.filter((r) => String(r.id) !== String(id)));
        setFavoritesCount((c) => Math.max(0, c - 1));
        return true;
      }
    } catch (e) {
      // fallback to localStorage
      const favs = JSON.parse(window.localStorage.getItem('favorites') || '[]');
      const next = favs.filter((f) => f.title !== title);
      window.localStorage.setItem('favorites', JSON.stringify(next));
      setFavorites(next);
      setFavoritesCount(next.length);
      return true;
    }
  };

  const handleAuthSuccess = (userData) => {
    setToken(userData.token);
    window.localStorage.setItem('token', userData.token);
    setAuthUser({ id: userData.id, username: userData.username });
    setShowWelcome(false);
    setAuthError(null);
    
    // Fetch favorites after successful auth
    setTimeout(() => {
      fetchFavorites(userData.token);
      getRecipes(searchTerm);
    }, 100);
  };

  const logout = () => {
    setToken(null);
    setAuthUser(null);
    window.localStorage.removeItem('token');
    setShowWelcome(true);
    setRecipes([]);
    setFavorites([]);
    setFavoritesCount(0);
  };

  const getRecipes = async (query) => {
    const q = query || searchTerm;

    if (!APP_ID || !APP_KEY) {
      setError('Missing API credentials. Set REACT_APP_EDAMAM_ID and REACT_APP_EDAMAM_KEY');
      return;
    }

    // Validate API credentials format
    if (APP_ID.length < 8 || APP_KEY.length < 32) {
      setError('Invalid API credentials format. Check your REACT_APP_EDAMAM_ID and REACT_APP_EDAMAM_KEY');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const apiUrl = `https://api.edamam.com/search?q=${encodeURIComponent(q)}&app_id=${APP_ID}&app_key=${APP_KEY}`;
      
      // Validate HTTPS endpoint
      if (!apiUrl.startsWith('https://')) {
        throw new Error('API endpoint must use HTTPS for security');
      }

      const response = await fetch(apiUrl);

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setRecipes(data.hits || []);
    } catch (err) {
      setError(err.message || 'Failed to fetch recipes');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    // Only fetch default recipes if not showing welcome page
    if (!showWelcome) {
      getRecipes(searchTerm);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [showWelcome]);

  const handleSearch = (event) => setSearchTerm(event.target.value);

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    getRecipes(searchTerm);
  };

  // Show welcome page if user is not authenticated
  if (showWelcome) {
    return (
      <WelcomePage 
        onAuthSuccess={handleAuthSuccess}
        isLoading={isAuthLoading}
        setIsLoading={setIsAuthLoading}
        error={authError}
        setError={setAuthError}
      />
    );
  }

  return (
    <div className="App">
      <header className="header">
        <div className="brand">
          <div className="logo">R</div>
          <h1>Recipe Haven</h1>
        </div>

        <form className="search-form" onSubmit={handleSearchSubmit}>
          <input
            className={`search-bar ${isSearchFocused ? 'focused' : ''}`}
            type="text"
            value={searchTerm}
            onChange={handleSearch}
            onFocus={() => setIsSearchFocused(true)}
            onBlur={() => setIsSearchFocused(false)}
            placeholder="Search recipes, e.g. chicken, pasta, salad"
          />
          <div className="controls">
            <button className="search-button" type="submit" disabled={isLoading}>
              {isLoading ? 'üîç Searching...' : 'üîç Search'}
            </button>
            <div 
              className="fav-count interactive" 
              onClick={() => setShowSaved(true)}
              title="View saved recipes"
            >
              ‚ù§Ô∏è {favoritesCount}
            </div>
          </div>
        </form>

        <div style={{marginLeft:12}}>
          {authUser && (
            <button 
              className="btn interactive" 
              onClick={() => setShowCreate(true)}
              title="Create a new recipe"
            >
              ‚ûï New Recipe
            </button>
          )}
        </div>

        <div className="auth-controls">
          {authUser && (
            <div className="auth-area">
              <span className="auth-user">Hi, {authUser.username}</span>
              <button className="btn" type="button" onClick={logout}>Logout</button>
            </div>
          )}
        </div>
      </header>

      {isLoading && (
        <div className="empty-card loading">
          <div style={{fontSize: '2rem', marginBottom: '16px'}}>üç≥</div>
          <div>Searching for delicious recipes...</div>
          <div style={{fontSize: '0.9rem', opacity: '0.7', marginTop: '8px'}}>This might take a moment</div>
        </div>
      )}
      
      {error && (
        <div className="empty-card" style={{borderColor: 'var(--danger)', background: 'rgba(239, 68, 68, 0.1)'}}>
          <div style={{fontSize: '2rem', marginBottom: '16px'}}>‚ö†Ô∏è</div>
          <div>Oops! Something went wrong</div>
          <div style={{fontSize: '0.9rem', opacity: '0.8', marginTop: '8px'}}>{error}</div>
          <button 
            className="btn" 
            style={{marginTop: '16px'}} 
            onClick={() => getRecipes(searchTerm)}
          >
            üîÑ Try Again
          </button>
        </div>
      )}

      <div className="recipes">
        {recipes.length === 0 && !isLoading && !error && (
          <div className="empty-card">
            <div style={{fontSize: '3rem', marginBottom: '16px'}}>üîç</div>
            <div style={{fontSize: '1.2rem', marginBottom: '8px'}}>No recipes found</div>
            <div style={{fontSize: '0.9rem', opacity: '0.7'}}>Try searching for something else like "pasta", "salad", or "dessert"</div>
            <div style={{display: 'flex', gap: '8px', marginTop: '16px', flexWrap: 'wrap', justifyContent: 'center'}}>
              {['pasta', 'chicken', 'salad', 'dessert', 'soup'].map(suggestion => (
                <button 
                  key={suggestion}
                  className="btn" 
                  style={{fontSize: '0.8rem', padding: '6px 12px'}}
                  onClick={() => {
                    setSearchTerm(suggestion);
                    getRecipes(suggestion);
                  }}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          </div>
        )}

        {recipes.map((recipeItem) => (
          <Recipe
            key={recipeItem.recipe.uri}
            title={recipeItem.recipe.label}
            calories={recipeItem.recipe.calories}
            image={recipeItem.recipe.image}
            ingredients={recipeItem.recipe.ingredients}
            saved={favorites.some((f) => f.title === recipeItem.recipe.label)}
            onSave={() => saveRecipe({ title: recipeItem.recipe.label, image: recipeItem.recipe.image, calories: recipeItem.recipe.calories, ingredients: recipeItem.recipe.ingredients })}
            onRemove={() => {
              const found = favorites.find((f) => f.title === recipeItem.recipe.label);
              if (found) removeRecipe(found.id, found.title);
            }}
          />
        ))}

        <SavedRecipes open={showSaved} onClose={()=>setShowSaved(false)} favorites={favorites} token={token} refresh={fetchFavorites} />

        <CreateRecipeModal open={showCreate} onClose={()=>setShowCreate(false)} token={token} onCreated={async ()=>{
          await fetchFavorites();
          setShowCreate(false);
        }} />
      </div>

      <div className="footer-note">
        <div style={{marginBottom: '8px'}}>
          üç≥ Built with love using the Edamam API
        </div>
        <div style={{fontSize: '0.8rem', opacity: '0.6'}}>
          Add your credentials in <code>.env</code> to get started
        </div>
      </div>
    </div>
  );
};

export default App;
