import React, { useState, useRef, useEffect } from "react";
import "./WelcomePage.css";

const WelcomePage = ({
  onAuthSuccess,
  isLoading,
  setIsLoading,
  error,
  setError,
}) => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const calculateStrength = (pw) => {
    let score = 0;
    if (!pw) return 0;
    if (pw.length >= 8) score++;
    if (/[A-Z]/.test(pw)) score++;
    if (/[0-9]/.test(pw)) score++;
    if (/[^A-Za-z0-9]/.test(pw)) score++;
    return Math.min(3, score);
  };

  const strengthLabels = ["", "Weak", "Fair", "Strong"];
  const passwordStrength = calculateStrength(password);
  const passwordStrengthLabel = strengthLabels[passwordStrength];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError("Please fill in all fields");
      return;
    }

    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    // For sign up, enforce a strong password (score 3)
    if (!isLogin && passwordStrength < 3) {
      setError("Please choose a stronger password: min 8 chars, include uppercase, number and symbol.");
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const endpoint = isLogin ? '/api/auth/login' : '/api/auth/signup';
      console.log('Attempting to connect to:', endpoint);

      const res = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      console.log('Response status:', res.status);
      const data = await res.json();
      console.log('Response data:', data);

      if (!res.ok) {
        setError(data?.error || `${isLogin ? "Login" : "Signup"} failed`);
        return;
      }

      onAuthSuccess(data);
    } catch (err) {
      console.error('Network error details:', err);
      setError(`Connection failed. Make sure the server is running on the correct port.`);
    } finally {
      setIsLoading(false);
    }
  };

  const containerRef = useRef(null);

  const switchMode = (next) => {
    const target = typeof next === 'boolean' ? next : !isLogin;
    setIsLogin(target);
    setError(null);
    setPassword("");
    setConfirmPassword("");
    setShowPassword(false);
  };

  useEffect(() => {
    // Respect reduced-motion preference
    if (typeof window === 'undefined') return;
    if (window.matchMedia && window.matchMedia('(prefers-reduced-motion: reduce)').matches) return;

    const el = containerRef.current;
    if (!el) return;

    let raf = null;

    const handleMove = (e) => {
      const rect = el.getBoundingClientRect();
      const cx = rect.left + rect.width / 2;
      const cy = rect.top + rect.height / 2;
      const dx = Math.max(-1, Math.min(1, (e.clientX - cx) / (rect.width / 2)));
      const dy = Math.max(-1, Math.min(1, (e.clientY - cy) / (rect.height / 2)));

      const px = (dx * 12).toFixed(2) + 'px';
      const py = (dy * 8).toFixed(2) + 'px';

      if (raf) cancelAnimationFrame(raf);
      raf = requestAnimationFrame(() => {
        el.style.setProperty('--par-x', px);
        el.style.setProperty('--par-y', py);
      });
    };

    const handleLeave = () => {
      if (raf) cancelAnimationFrame(raf);
      el.style.setProperty('--par-x', '0px');
      el.style.setProperty('--par-y', '0px');
    };

    el.addEventListener('mousemove', handleMove);
    el.addEventListener('mouseleave', handleLeave);

    return () => {
      el.removeEventListener('mousemove', handleMove);
      el.removeEventListener('mouseleave', handleLeave);
      if (raf) cancelAnimationFrame(raf);
    };
  }, []); 

  const canSubmit = isLogin ? (username && password) : (username && password && confirmPassword && password === confirmPassword && passwordStrength === 3);

  return (
    <div className="welcome-page">
      <div className="welcome-background">
        {["üçï", "üçù", "ü•ó", "üç∞", "üçú", "ü•ò"].map((f, i) => (
          <div key={i} className="floating-food">{f}</div>
        ))}
      </div>

      <div className="welcome-container" ref={containerRef}>
        <div className="welcome-header">
          <div className="welcome-logo">
            <div className="logo-icon">RH</div>
            <div className="title-row">
              <h1>Recipe Haven</h1>
              <span className="dev-badge">Developed by Rounak</span>
            </div>
            <p className="welcome-subtitle">
              Discover, save, and create amazing recipes from around the world ‚Äî smart suggestions, personal collections, and effortless recipe creation.
            </p>

            <div className="quick-features" aria-hidden>
              <div className="chip">üîç Smart Search</div>
              <div className="chip">‚ù§Ô∏è Save & Sync</div>
              <div className="chip">‚úçÔ∏è Create & Share</div>
            </div>
          </div>

          <div className="header-blob" aria-hidden></div>
        </div>

        <div className="auth-card">
          <div className="auth-tabs">
            <button
              className={`auth-tab ${isLogin ? "active" : ""}`}
              onClick={() => switchMode(true)}
            >
              Sign In
            </button>
            <button
              className={`auth-tab ${!isLogin ? "active" : ""}`}
              onClick={() => switchMode(false)}
            >
              Sign Up
            </button>
          </div>



          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label>Username</label>
              <input
                className="form-input"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                disabled={isLoading}
                placeholder="Enter your username"
              />
            </div>

            <div className="form-group password-group">
              <label>Password</label>
              <div className="password-field">
                <input
                  className="form-input"
                  type={showPassword ? "text" : "password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  disabled={isLoading}
                  placeholder="Enter your password"
                  aria-label="Password"
                />
                <button
                  type="button"
                  className={`show-pass-btn ${showPassword ? 'uncovered' : 'covered'}`}
                  onClick={() => setShowPassword(!showPassword)}
                  aria-pressed={showPassword}
                  aria-label={showPassword ? "Hide password" : "Show password"}
                  title={showPassword ? "Hide password" : "Show password"}
                >
                    <span className="emoji" aria-hidden>{showPassword ? 'üêµ' : 'üôà'}</span>
                </button>
              </div>

              {!isLogin && (
                <>
                  <div className="strength-meter">
                    <div className={`strength-bar s${passwordStrength}`} aria-hidden></div>
                    <div className="strength-label">{password ? passwordStrengthLabel : ""}</div>
                  </div>

                  {password && passwordStrength < 3 && (
                    <div style={{fontSize: '0.85rem', marginTop: 8, color: 'rgba(239,68,68,0.9)'}}>Password must be strong (min 8 chars, include uppercase, number and symbol)</div>
                  )}
                </>
              )}
            </div>

            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  className="form-input"
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  disabled={isLoading}
                  placeholder="Confirm your password"
                />
              </div>
            )}

            {error && <div className="error-message">‚ö†Ô∏è {error}</div>}

            <button type="submit" disabled={isLoading || !canSubmit} className="auth-submit-btn">
              {isLoading
                ? isLogin
                  ? "Signing In..."
                  : "Creating Account..."
                : isLogin
                ? "üöÄ Sign In"
                : "‚ú® Create Account"}
            </button>

            <div style={{display: 'flex', gap: 8, justifyContent: 'center', marginTop: 12}}>
              <button
                type="button"
                className="auth-guest-btn"
                onClick={() => {
                  setError(null);
                  setIsLoading(false);
                  onAuthSuccess({ token: '', id: 'guest', username: 'Guest' });
                }}
              >
                Continue as Guest
              </button>
            </div>
          </form>

          <div className="auth-switch">
            <p>
              {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
              <button onClick={switchMode} className="switch-btn">
                {isLogin ? "Sign up here" : "Sign in here"}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomePage;
